import './App.css';
import TopBar from './components/topbar';
import banner1 from './assets/document.jpeg'
import banner2 from './assets/Designer (2).png'
import banner3 from './assets/Designer (3).png'
import Companies from './components/companies';
import { useState } from 'react';
import math from 'mathjs';

function App() {
  const neutral = {
    banner:  `${banner1}`,
    background: '#F4F4F4',
    topbar: 'bg-neutral-300',

  }
  const dark = {
    banner:  `${banner2}`,
    background: '#1F1F1F',
    topbar: 'bg-zinc-100',
  }
  const light = {
    banner:  `${banner3}`,
    background: '#FFFFFF',
    topbar: 'bg-zinc-100',
  }
  const [theme, setTheme] = useState(neutral);

  const randomNumber = math.floor(math.random()*3)
  switch(randomNumber){
    case 0: setTheme(neutral);
    break;
    case 1: setTheme(dark);
    break;
    case 2: setTheme(light);
    break;
    default: setTheme(neutral);
  }

  return (
  <div className='flex-1 flex-col justify-items-center max-h-full'>
    <TopBar />
    <div className='flex flex-row  items-center'>
    <img src={theme.banner} alt='banner' className='my-5'/>
    <Companies />
    </div>
  </div>  );
}

export default App;
